package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import java.io.Serializable;import java.math.BigDecimal;import java.util.Objects;@Entity@Table(name = "aparelhos")@SequenceGenerator(name = "seq_aparelhos", sequenceName = "seq_aparelhos", initialValue = 1, allocationSize = 1)public class Aparelho implements Serializable, EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_aparelhos")    private Long id;    @Column(name = "descricao", length = 255, nullable = false)    private String descricao;    @Column(name = "potencia", nullable = false)    private BigDecimal potencia;    @Column(name = "tensao")    private Integer tensao;    @Override    public Long getId() {        return id;    }    public String getDescricao() {        return descricao;    }    public void setDescricao(String descricao) {        this.descricao = descricao;    }    public BigDecimal getPotencia() {        return potencia;    }    public void setPotencia(BigDecimal potencia) {        this.potencia = potencia;    }    public Integer getTensao() {        return tensao;    }    public void setTensao(Integer tensao) {        this.tensao = tensao;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Aparelho aparelho = (Aparelho) o;        return Objects.equals(id, aparelho.id) &&                Objects.equals(descricao, aparelho.descricao) &&                Objects.equals(potencia, aparelho.potencia) &&                Objects.equals(tensao, aparelho.tensao);    }    @Override    public int hashCode() {        return Objects.hash(id, descricao, potencia, tensao);    }    @Override    public String toString() {        return "Aparelho{" +                "id=" + id +                ", descricao='" + descricao + '\'' +                ", potencia=" + potencia +                ", tensao=" + tensao +                '}';    }}
