package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.math.BigDecimal;@Entity@Table(name = "calculo_cargas")@SequenceGenerator(name = "seq_calculo_cargas", sequenceName = "seq_calculo_cargas", initialValue = 1, allocationSize = 1)public class CalculoCarga implements EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_calculo_cargas")    private Long id;    @NotNull(message = "{CalculoCarga.i_aparelho.NotNull}")    @ManyToOne    @JoinColumn(name = "i_aparelho", nullable = false)    private Aparelho aparelho;    @NotNull(message = "{CalculoCarga.corrente.NotNull}")    @Column(name = "corrente")    private BigDecimal corrente;    @NotNull(message = "{CalculoCarga.protecao.NotNull}")    @Column(name = "protecao")    private BigDecimal protecao;    @NotNull(message = "{CalculoCarga.diametro_condutor.NotNull}")    @Column(name = "diametro_condutor")    private BigDecimal diametroCondutor;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Aparelho getAparelho() {        return aparelho;    }    public void setAparelho(Aparelho aparelho) {        this.aparelho = aparelho;    }    public BigDecimal getCorrente() {        return corrente;    }    public void setCorrente(BigDecimal corrente) {        this.corrente = corrente;    }    public BigDecimal getProtecao() {        return protecao;    }    public void setProtecao(BigDecimal protecao) {        this.protecao = protecao;    }    public BigDecimal getDiametroCondutor() {        return diametroCondutor;    }    public void setDiametroCondutor(BigDecimal diametroCondutor) {        this.diametroCondutor = diametroCondutor;    }}