package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.math.BigDecimal;import java.util.List;@Entity@Table(name = "calculo_circuitos")@SequenceGenerator(name = "seq_calculo_circuitos", sequenceName = "seq_calculo_circuitos", initialValue = 1, allocationSize = 1)public class CalculoCircuito implements EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_calculo_circuitos")    private Long id;    @NotNull(message = "{CalculoCircuito.descricao.NotNull}")    @Size(min = 1, max = 255, message = "{CalculoCircuito.descricao.Size}")    @Column(name = "descricao", length = 255, nullable = false)    private String descricao;    @Column(name = "potencia")    private BigDecimal potencia;    @Column(name = "corrente")    private BigDecimal corrente;    @JoinColumn(name = "id_circuito")    @OneToMany(fetch = FetchType.EAGER, cascade = CascadeType.ALL, orphanRemoval = true)    private List<CalculoCircuitoItem> itens;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getDescricao() {        return descricao;    }    public void setDescricao(String descricao) {        this.descricao = descricao;    }    public BigDecimal getPotencia() {        return potencia;    }    public void setPotencia(BigDecimal potencia) {        this.potencia = potencia;    }    public BigDecimal getCorrente() {        return corrente;    }    public void setCorrente(BigDecimal corrente) {        this.corrente = corrente;    }    public List<CalculoCircuitoItem> getItens() {        return itens;    }    public void setItens(List<CalculoCircuitoItem> itens) {        this.itens = itens;    }}