package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;@Entity@Table(name = "calculo_circuito_itens")@SequenceGenerator(name = "seq_calculo_circuito_itens", sequenceName = "seq_calculo_circuito_itens", initialValue = 1, allocationSize = 1)public class CalculoCircuitoItem implements EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_calculo_circuito_itens")    private Long id;    @NotNull(message = "{CalculoCircuitoItem.i_aparelho.NotNull}")    @ManyToOne    @JoinColumn(name = "i_aparelho", nullable = false)    private Aparelho aparelho;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Aparelho getAparelho() {        return aparelho;    }    public void setAparelho(Aparelho aparelho) {        this.aparelho = aparelho;    }}