package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;import java.io.Serializable;import java.math.BigDecimal;@Entity@Table(name = "calculo_corrente")@SequenceGenerator(name = "seq_calculo_corrente", sequenceName = "seq_calculo_corrente", initialValue = 1, allocationSize = 1)public class CalculoCorrente implements Serializable, EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_calculo_corrente")    private Long id;    @NotNull    @ManyToOne    @JoinColumn(name = "i_aparelho", nullable = false)    private Aparelho aparelho;    @NotNull    @Column(name = "corrente")    private BigDecimal corrente;    @Column(name = "corrente_protecao")    private BigDecimal protecao;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Aparelho getAparelho() {        return aparelho;    }    public void setAparelho(Aparelho aparelho) {        this.aparelho = aparelho;    }    public BigDecimal getCorrente() {        return corrente;    }    public void setCorrente(BigDecimal corrente) {        this.corrente = corrente;    }    public BigDecimal getProtecao() {        return protecao;    }    public void setProtecao(BigDecimal protecao) {        this.protecao = protecao;    }}