package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import java.io.Serializable;import java.math.BigDecimal;@Entity@Table(name = "cargas")@SequenceGenerator(name = "seq_cargas", sequenceName = "seq_cargas", initialValue = 1, allocationSize = 1)public class Carga implements Serializable, EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_cargas")    private Long id;    @Column(name = "total_corrente")    private BigDecimal totalCorrente;    @Column(name = "total_carga")    private BigDecimal totalCarga;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public BigDecimal getTotalCorrente() {        return totalCorrente;    }    public void setTotalCorrente(BigDecimal totalCorrente) {        this.totalCorrente = totalCorrente;    }    public BigDecimal getTotalCarga() {        return totalCarga;    }    public void setTotalCarga(BigDecimal totalCarga) {        this.totalCarga = totalCarga;    }}