package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.math.BigDecimal;@Entity@Table(name = "condutores")@SequenceGenerator(name = "seq_condutores", sequenceName = "seq_condutores", initialValue = 1, allocationSize = 1)public class Condutor implements EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_condutores")    private Long id;    @NotNull(message = "{Condutor.descricao.NotNull}")    @Size(min = 1, max = 255, message = "{Condutor.descricao.Size}")    @Column(name = "descricao", length = 255, nullable = false)    private String descricao;    @NotNull(message = "{Condutor.tipo.NotNull}")    @Column(name = "tipo", nullable = false)    private String tipo;    @NotNull(message = "{Condutor.cor.NotNull}")    @Column(name = "cor", nullable = false)    private String cor;    @NotNull(message = "{Condutor.espessura.NotNull}")    @Column(name = "espessura", nullable = false)    private BigDecimal espessura;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getDescricao() {        return descricao;    }    public void setDescricao(String descricao) {        this.descricao = descricao;    }    public String getTipo() {        return tipo;    }    public void setTipo(String tipo) {        this.tipo = tipo;    }    public String getCor() {        return cor;    }    public void setCor(String cor) {        this.cor = cor;    }    public BigDecimal getEspessura() {        return espessura;    }    public void setEspessura(BigDecimal espessura) {        this.espessura = espessura;    }}