package com.vandersoncamp.ampereweb.model;import com.vandersoncamp.ampereweb.util.EntityID;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;@Entity@Table(name = "usuarios")@SequenceGenerator(name = "seq_usuarios", sequenceName = "seq_usuarios", initialValue = 1, allocationSize = 1)public class Usuario implements EntityID {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_usuarios")    private Long id;    @NotNull(message = "{Usuario.nome.NotNull}")    @Size(min = 1, max = 100, message = "{Usuario.nome.Size}")    @Column(name = "nome", length = 100, nullable = false)    private String nome;    @Column(name = "telefone")    private Long telefone;    @NotNull(message = "{Usuario.tipo.NotNull}")    @Column(name = "tipo", nullable = false)    @Enumerated(EnumType.ORDINAL)    private TipoUsuarioEnum tipo;    @Column(name = "email", length = 100)    private String email;    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public Long getTelefone() {        return telefone;    }    public void setTelefone(Long telefone) {        this.telefone = telefone;    }    public TipoUsuarioEnum getTipo() {        return tipo;    }    public void setTipo(TipoUsuarioEnum tipo) {        this.tipo = tipo;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}