package com.vandersoncamp.ampereweb.resource;import com.vandersoncamp.ampereweb.model.CalculoCorrente;import com.vandersoncamp.ampereweb.service.CalculoCorrenteService;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/calculo-corrente")@RequestScopedpublic class CalculoCorrenteResource {    @Inject    private CalculoCorrenteService service;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        CalculoCorrente calculoCorrente = service.buscar(id);        if (calculoCorrente == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(calculoCorrente).build();    }    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(service.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(CalculoCorrente calculoCorrente) {        service.criar(calculoCorrente);        return Response.status(Response.Status.CREATED).entity(calculoCorrente).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(CalculoCorrente calculoCorrente, @PathParam("id") Long id) {        if (!id.equals(calculoCorrente.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        service.atualizar(calculoCorrente);        return Response.status(Response.Status.OK).entity(calculoCorrente).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        service.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}