package com.vandersoncamp.ampereweb.resource;import com.vandersoncamp.ampereweb.model.ConducaoCorrente;import com.vandersoncamp.ampereweb.service.ConducaoCorrenteService;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/conducaocorrentes")@RequestScopedpublic class ConducaoCorrenteResource {    @Inject    private ConducaoCorrenteService service;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        ConducaoCorrente conducaoCorrente = service.buscar(id);        if (conducaoCorrente == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(conducaoCorrente).build();    }    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(service.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(ConducaoCorrente conducaoCorrente) {        service.criar(conducaoCorrente);        return Response.status(Response.Status.CREATED).entity(conducaoCorrente).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(ConducaoCorrente conducaoCorrente, @PathParam("id") Long id) {        if (!id.equals(conducaoCorrente.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        service.atualizar(conducaoCorrente);        return Response.status(Response.Status.OK).entity(conducaoCorrente).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        service.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}