package com.vandersoncamp.ampereweb.resource;import com.vandersoncamp.ampereweb.model.Usuario;import com.vandersoncamp.ampereweb.service.UsuarioService;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.Consumes;import javax.ws.rs.POST;import javax.ws.rs.Path;import javax.ws.rs.Produces;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/usuarios")@RequestScopedpublic class UsuarioResource {    @Inject    private UsuarioService service;    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Usuario usuario) {        service.criar(usuario);        return Response.status(Response.Status.CREATED).entity(usuario).build();    }}