package com.vandersoncamp.ampereweb.rest.resource;import com.vandersoncamp.ampereweb.model.Aparelho;import com.vandersoncamp.ampereweb.service.AparelhoService;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/aparelhos")@RequestScopedpublic class AparelhoResource {    @Inject    private AparelhoService service;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Aparelho aparelho = service.buscar(id);        if (aparelho == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(aparelho).build();    }    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(service.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Aparelho aparelho) {        service.criar(aparelho);        return Response.status(Response.Status.CREATED).entity(aparelho).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Aparelho aparelho, @PathParam("id") Long id) {        if (!id.equals(aparelho.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        service.atualizar(aparelho);        return Response.status(Response.Status.OK).entity(aparelho).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        service.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}