package com.vandersoncamp.ampereweb.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.math.BigDecimal;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;public class AparelhoTest {    private Aparelho aparelho() {        Aparelho aparelho = new Aparelho();        aparelho.setId(1L);        aparelho.setDescricao("Aparelho teste");        aparelho.setPotencia(BigDecimal.TEN);        aparelho.setTensao(220);        return aparelho;    }    @Test    @DisplayName("Test id")    public void test01() {        assertNotNull(aparelho().getId());    }    @Test    @DisplayName("Test descrição")    public void test02() {        assertEquals(aparelho().getDescricao(), "Aparelho teste");    }    @Test    @DisplayName("Test potência")    public void test03() {        assertEquals(aparelho().getPotencia(), BigDecimal.TEN);    }}