package com.vandersoncamp.ampereweb.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.math.BigDecimal;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;public class CalculoCargaTest {    private Aparelho aparelho() {        Aparelho aparelho = new Aparelho();        aparelho.setId(1L);        aparelho.setDescricao("Aparelho teste");        return  aparelho;    }    private CalculoCarga calculoCarga() {        CalculoCarga calculoCarga = new CalculoCarga();        calculoCarga.setId(1L);        calculoCarga.setAparelho(aparelho());        calculoCarga.setCorrente(BigDecimal.TEN);        calculoCarga.setDiametroCondutor(BigDecimal.ONE);        calculoCarga.setProtecao(BigDecimal.ONE);        return calculoCarga;    }    @Test    @DisplayName("Test id")    public void test01() {        assertNotNull(calculoCarga().getId());    }    @Test    @DisplayName("Test aparelho")    public void test02() {        assertEquals(calculoCarga().getAparelho().getDescricao(), "Aparelho teste");    }    @Test    @DisplayName("Test corrente")    public void test03() {        assertEquals(calculoCarga().getCorrente(), BigDecimal.TEN);    }    @Test    @DisplayName("Test diametro condutor")    public void test04() {        assertEquals(calculoCarga().getDiametroCondutor(), BigDecimal.ONE);    }    @Test    @DisplayName("Test diametro condutor")    public void test05() {        assertEquals(calculoCarga().getProtecao(), BigDecimal.ONE);    }}