package com.vandersoncamp.ampereweb.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.math.BigDecimal;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;public class CalculoCorrenteTest {    private Aparelho aparelho() {        Aparelho aparelho = new Aparelho();        aparelho.setId(1L);        aparelho.setDescricao("Aparelho teste");        return  aparelho;    }    private CalculoCorrente calculoCorrente() {        CalculoCorrente calculoCorrente = new CalculoCorrente();        calculoCorrente.setId(1L);        calculoCorrente.setAparelho(aparelho());        calculoCorrente.setCorrente(BigDecimal.TEN);        calculoCorrente.setDistancia(BigDecimal.TEN);        calculoCorrente.setQuedaTensao(BigDecimal.ONE);        return calculoCorrente;    }    @Test    @DisplayName("Test id")    public void test01() {        assertNotNull(calculoCorrente().getId());    }    @Test    @DisplayName("Test aparelho")    public void test02() {        assertEquals(calculoCorrente().getAparelho().getDescricao(), "Aparelho teste");    }    @Test    @DisplayName("Test corrente")    public void test03() {        assertEquals(calculoCorrente().getCorrente(), BigDecimal.TEN);    }    @Test    @DisplayName("Test distância")    public void test04() {        assertEquals(calculoCorrente().getDistancia(), BigDecimal.TEN);    }    @Test    @DisplayName("Test queda tensão")    public void test05() {        assertEquals(calculoCorrente().getQuedaTensao(), BigDecimal.ONE);    }}