package com.vandersoncamp.ampereweb.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.math.BigDecimal;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;public class CondutorTest {    private Condutor condutor() {        Condutor condutor = new Condutor();        condutor.setId(1L);        condutor.setDescricao("Condutor teste");        condutor.setCor("Azul");        condutor.setEspessura(BigDecimal.ONE);        condutor.setTipo("Flexível");        return condutor;    }    @Test    @DisplayName("Test id")    public void test01() {        assertNotNull(condutor().getId());    }    @Test    @DisplayName("Test descrição")    public void test02() {        assertEquals(condutor().getDescricao(), "Condutor teste");    }    @Test    @DisplayName("Test cor")    public void test03() {        assertEquals(condutor().getCor(), "Azul");    }    @Test    @DisplayName("Test espessura")    public void test04() {        assertEquals(condutor().getEspessura(), BigDecimal.ONE);    }    @Test    @DisplayName("Test tipo")    public void test05() {        assertEquals(condutor().getTipo(), "Flexível");    }}